package cn.zthz.tool.cache.user;

public class UserCacheKeys {
	
	public static String userStatus(String userId) {
		return "user." + userId + ".status";
	}

	public static String userToken(String userId) {
		return "user." + userId + ".token";
	}

	public static String userInfo(String userId) {
		return "user." + userId + ".info";
	}
	public static String userDevice(String userId) {
		return "user." + userId + ".device";
	}
	public static String allowPush(String userId) {
		return "user." + userId + ".allowPush";
	}
	public static String allowPushNewRequirement(String userId) {
		return "user." + userId + ".allowPushNewRequirement";
	}
}
